import axios from 'axios'
import React, { useState, useEffect, setStatus } from 'react'
import { Link } from 'react-router-dom'
import { Nav, Navbar, NavItem, NavbarBrand, Row, Col } from 'reactstrap'
import data from './data.js'

function Home() {
  const [users, setUsers] = useState([])

  const deleteUser = (id) => {
    axios
      .delete(`${process.env.REACT_APP_API_URL}api/delete/${id}`)
      .then((res) => {
        console.log(res)
        getUsers()
      })
      .catch((err) => console.log(err))
  }

  const getUsers = () => {
    axios
      .get(`${process.env.REACT_APP_API_URL}api/index`)
      .then((res) => {
        if (res.data.status === 200) {
          setUsers(res.data.users)
        } else {
          console.log('error')
        }
        if (res.data.users) {
          setUsers(res.data.users)
        } else {
          setUsers([])
        }
        console.log(res.data.users)
      })
      .catch((err) => console.log(err))
  }

  useEffect(() => {
    getUsers()
  }, [])

  //   useEffect(() => {
  //     // DELETE request using axios inside useEffect React hook
  //     axios
  //       .delete(`${process.env.REACT_APP_API_URL}api/delete/${id}`)
  //       .then(() => setStatus('Delete successful'))
  //   }, [])

  return (
    <div className="container home">
      <Navbar color="dark" dark className="mb-3">
        <Row className="w-100 mx-3">
          <Col lg={10}>
            <NavbarBrand href="/home"> OAS Team</NavbarBrand>
          </Col>
          <Col lg={2}>
            <Nav>
              <NavItem>
                <Link className="btn btn-primary" to="/add">
                  Add User
                </Link>
              </NavItem>
            </Nav>
          </Col>
        </Row>
      </Navbar>

      <table className="table table-striped border ">
        <thead className=" bg-dark text-white ">
          <tr>
            <th scope="col">Id</th>
            <th scope="col">Username</th>
            <th scope="col">Email</th>
            <th scope="col">Operations</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user, index) => {
            return (
              <tr key={index}>
                <th scope="row">{user.id}</th>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>
                  <button
                    type="button"
                    className="btn btn-danger"
                    onClick={() => deleteUser(user.id)}>
                    Delete
                  </button>
                </td>
              </tr>
            )
          })}
        </tbody>
      </table>
    </div>
  )
}

export default Home
